function relogio(){var o=new Date,t=6*o.getSeconds(),e=6*o.getMinutes()+t/60,r=15*o.getHours()+e/12,s="rotate("+t+"deg)",i="rotate("+e+"deg)",n="rotate("+r+"deg)";$("#segundo").css({transform:s}),$("#minuto").css({transform:i}),$("#hora").css({transform:n})}$("#mostrar").hide(),$(document).ready(function(){setInterval(relogio,1e3),$("#ocultar").click(function(){$(this).hide(),$("#mostrar").show(),$(".relogio").hide()}),$("#mostrar").click(function(){$(this).hide(),$("#ocultar").show(),$(".relogio").show()})});